import { useEffect, useState } from "react";

import './App.css';
import searchIcon from './search.svg';

import MovieCard from "./MovieCard";
//2aefdf6b

const API_URL = 'http://www.omdbapi.com?apikey=2aefdf6b';


const App = ()=>{
    const [movies, setMovies] = useState([]);
    const searchMovies = async (title) => {
        const response = await fetch(`${API_URL}&s=${title}`);
        const data = await response.json();
        console.log(data.Search);
        setMovies(data.Search);
    }
    useEffect(()=>{
        searchMovies('Batman');
    },[]);//this [] is used if you want this hook to fire only at the start of the app
    const movie1 = {
        "Title": "Batman Begins",
        "Year": "2005",
        "imdbID": "tt0372784",
        "Type": "movie",
        "Poster": "https://m.media-amazon.com/images/M/MV5BOTY4YjI2N2MtYmFlMC00ZjcyLTg3YjEtMDQyM2ZjYzQ5YWFkXkEyXkFqcGdeQXVyMTQxNzMzNDI@._V1_SX300.jpg"
    };





    return (
        <div className="app">
            <h1>PK Movies</h1>
            <div className="search">
                <input type="text" placeholder="search for movies" value='Superman' onChange={()=>{}}/>
                <img src={searchIcon} alt="search" onClick={()=>{}}/>
            </div>
            {
                movies.length>0
                ? (
                    <div className="container">
                        {
                            movies.map((movie)=>(<MovieCard movie1 ={movie}/>))
                        }
                    </div>
                ) : (
                    <div className="empty">
                        <h2>No movies found!</h2>
                    </div>
                )
            }
            
        </div>
    )
};

export default App;
